import React from 'react';
import './Hero.css';

const Hero3 = () => {
  const experiences = [
    {
      company: "Company A",
      role: "Software Engineer",
      description: "Worked on developing scalable web applications and contributed to multiple projects using React and Node.js.",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
    {
      company: "Company B",
      role: "Frontend Developer",
      description: "Specialized in creating responsive UI components with React and Material-UI, improving user engagement by 30%.",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
    {
      company: "Company C",
      role: "Backend Developer",
      description: "Designed and implemented RESTful APIs with Node.js and Express, enhancing data retrieval efficiency by 40%.",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
  ];

  return (
    <section className="hero">
      <h1>Experiences</h1>
      <div className="experiences-container">
        {experiences.map((exp, index) => (
          <div className="experience-card" key={index}>
            <div className="experience-header">
              <img src={exp.icon} alt={`${exp.company} logo`} className="experience-icon" />
              <h2>{exp.company}</h2>
            </div>
            <h3>{exp.role}</h3>
            <p>{exp.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Hero3;
