import React from 'react';
import './Hero.css';
import { 
  School as SchoolIcon, 
  Science as ScienceIcon, 
  Book as BookIcon 
} from '@mui/icons-material';

const Hero5 = () => {
  const researchExperiences = [
    {
      date: "March 2020 - June 2021",
      title: "Research Assistant at XYZ Institute",
      description: "Worked on AI and Machine Learning projects, focusing on neural network optimization and data analysis.",
      icon: <SchoolIcon style={{ fontSize: 40 }} />,
    },
    {
      date: "February 2024 - May 2024",
      title: "Research Apprentice at UCSF Memory and Aging Center under Dr. Jet Vonk",
      description: "Developed, integrated, and evaluated content units in Lingustic Pipelines to quantify and interpret semantic richness of textual data ",
      icon: <ScienceIcon style={{ fontSize: 40 }} />,
    },
    {
      date: "April 2024 - Current",
      title: "NLP Research at Haas School of Business under Prof. Biwen Zhang",
      description: "Developed BERT models on large-scale datasets, fine-tuning hyperparameters for accurate semantic categorization and understanding of semantic textual data.",
      icon: <BookIcon style={{ fontSize: 40 }} />,
    },
  ];

  return (
    <section className="hero">
      <h1>Research Experiences</h1>
      <div className="research-list">
        {researchExperiences.map((experience, index) => (
          <div className="research-item" key={index}>
            <div className="research-icon">{experience.icon}</div>
            <div className="research-details">
              <p className="research-date">{experience.date}</p>
              <h2 className="research-title">{experience.title}</h2>
              <p className="research-description">{experience.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Hero5;
