import React from 'react';
import './Hero.css';

import Bin95Logo from './assets/bin95.jpg';
import SAPLogo from './assets/sap.jpeg';
import BerkeleyLogo from './assets/ucb.jpeg';
import TaiyoLogo from './assets/taiy_logo.jpeg';
import RevoLogo from './assets/revocube.jpeg';
import PostoLogo from './assets/posto.jpeg';

const Hero3 = ({ setCurrentPage, prev, next }) => {
  const experiences = [
    { company: "SAP SE", role: "Development Intern", icon: SAPLogo },
    { company: "Posto-Social Inc.", role: "Software Engineer Intern", icon: PostoLogo },
    { company: "Bin95 Industrial Training", role: "Senior Contract Developer", icon: Bin95Logo },
    { company: "Taiyō.AI", role: "Contract Developer", icon: TaiyoLogo },
    { company: "Berkeley IT", role: "Student IT Technician", icon: BerkeleyLogo },
    { company: "Revocube Technologies", role: "Software Developer Intern", icon: RevoLogo },
  ];

  return (
    <section className="hero">
      <h1>Experiences</h1>
      <div className="experiences-grid">
        {experiences.map((exp, index) => (
          <div className="experience-item" key={index}>
            <img src={exp.icon} alt={`${exp.company} logo`} className="experience-icon" />
            <div className="experience-details">
              <h2>{exp.company}</h2>
              <h3>{exp.role}</h3>
            </div>
          </div>
        ))}
      </div>
      <div className="navigation-hint top-hint" onClick={() => setCurrentPage(1)}>{prev}</div>
      <div className="navigation-hint bottom-hint" onClick={() => setCurrentPage(3)}>{next}</div>
    </section>
  );
};

export default Hero3;
