// src/components/sections/Contact.js
import React, { useState, useEffect } from 'react';
import './Contact.css';

const Contact = () => {
    const [showEmail, setShowEmail] = useState(false);
    const [message, setMessage] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const [showEmailContent, setShowEmailContent] = useState(false);

    useEffect(() => {
        if (showEmail) {
            setIsTyping(true);
            // Show typing indicator for 1.5 seconds before showing the email
            const typingTimer = setTimeout(() => {
                setIsTyping(false);
                setShowEmailContent(true);
            }, 1500);
            return () => clearTimeout(typingTimer);
        } else {
            setIsTyping(false);
            setShowEmailContent(false);
        }
    }, [showEmail]);

    return (
        <div className="contact-card">
            <div className="contact-header">
                <h2>contact</h2>
            </div>
            <div className="contact-body">
                {showEmail ? (
                    <>
                        <div className="message-container">
                            <button className="email-button" onClick={() => setShowEmail(false)}>
                                hide email
                            </button>
                        </div>
                        <div className="message-container left">
                            {isTyping ? (
                                <div className="typing-indicator" />
                            ) : showEmailContent && (
                                <p className="email-display">sarvagya [at] berkeley [dot] edu</p>
                            )}
                        </div>
                    </>
                ) : (
                    <>
                        <div className="message-container">
                            <button className="email-button" onClick={() => setShowEmail(true)}>
                                show email
                            </button>
                        </div>
                    </>
                )}
            </div>
            <div className="message-bar">
                <input 
                    type="text" 
                    className="message-input" 
                    placeholder="Text Message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    disabled
                />
                <button className="send-button" disabled>
                    Send
                </button>
            </div>
        </div>
    );
};

export default Contact;
