import React, { useEffect, useRef } from 'react';
import './EmojiBackground.css';

// Updated emoji list with more visible options
const EMOJIS = ['🌟', '⭐', '💫', '✨', '💻', '🚀', '🤖', '⚡', '🔮', '🎮', '🖥️', '📱', '⚙️','🛠️'];

const EmojiBackground = () => {
    const containerRef = useRef(null);

    const createEmojiLine = () => {
        if (!containerRef.current) return;

        const emoji = EMOJIS[Math.floor(Math.random() * EMOJIS.length)];
        const line = document.createElement('div');
        line.className = 'emoji-line';
        line.innerHTML = emoji;

        const startX = Math.random() * window.innerWidth;
        const startY = Math.random() * window.innerHeight; // Random starting height
        const duration = 6 + Math.random() * 8;
        const delay = Math.random() * 1;
        const size = 30 + Math.random() * 30;

        line.style.left = `${startX}px`;
        line.style.top = `${startY}px`; // Set random starting height
        line.style.fontSize = `${size}px`;
        line.style.animation = `floatUp ${duration}s linear ${delay}s`;

        containerRef.current.appendChild(line);

        setTimeout(() => {
            if (line && line.parentNode === containerRef.current) {
                containerRef.current.removeChild(line);
            }
        }, (duration + delay) * 1000);
    };

    useEffect(() => {
        // Create more initial emojis
        for (let i = 0; i < 15; i++) {
            setTimeout(() => createEmojiLine(), i * 200);
        }

        // Create new emojis more frequently
        const interval = setInterval(createEmojiLine, 500);
        return () => clearInterval(interval);
    }, []);

    return <div ref={containerRef} className="emoji-background" />;
};

export default EmojiBackground;
