import React from 'react';
import './Hero.css';

const Hero3 = () => {
  const experiences = [
    {
      company: "SAP SE",
      role: "Development Intern",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
    {
      company: "Posto-Social Inc.",
      role: "Software Engineer Intern",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
    {
      company: "Industrial Training",
      role: "Senior Contract Developer",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
    {
      company: "Taiyō.AI",
      role: "Contract Developer",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
    {
      company: "Berkeley IT",
      role: "Student IT Technician",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
    {
      company: "Revocube Technologies Limited",
      role: "Software Developer Intern",
      icon: "https://via.placeholder.com/50", // Replace with actual icon URL
    },
  ];

  return (
    <section className="hero">
      <h1>Experiences</h1>
      <div className="experiences-grid">
        {experiences.map((exp, index) => (
          <div className="experience-item" key={index}>
            <img src={exp.icon} alt={`${exp.company} logo`} className="experience-icon" />
            <div className="experience-details">
              <h2>{exp.company}</h2>
              <h3>{exp.role}</h3>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Hero3;
