import React from 'react';
import './Sidebar.css';

const Sidebar = ({ setActiveSection }) => {
    const handleClick = (section) => {
        setActiveSection(section);
    };

    return (
        <div className="sidebar">
            <button className="sidebar-button" onClick={() => handleClick('home')}>Home</button>
            <button className="sidebar-button" onClick={() => handleClick('about')}>About</button>
            <button className="sidebar-button" onClick={() => handleClick('experiences')}>Experiences</button>
            <button className="sidebar-button" onClick={() => handleClick('projects')}>Projects</button>
            <button className="sidebar-button" onClick={() => handleClick('research')}>Research</button>
            <button className="sidebar-button" onClick={() => handleClick('contact')}>Contact</button>
        </div>
    );
};

export default Sidebar;
