import React, { useEffect, useRef } from 'react';
import './EmojiBackground.css';

const EMOJIS = ['ðŸ’»', 'ðŸš€', 'ðŸ¤–', 'âš¡', 'ðŸ”®', 'ðŸŽ®', 'ðŸŽ¯', 'ðŸ’¡', 'ðŸŽ¨', 'ðŸ”‹'];

const EmojiBackground = () => {
    const containerRef = useRef(null);

    const createEmojiLine = () => {
        if (!containerRef.current) return;

        const emoji = EMOJIS[Math.floor(Math.random() * EMOJIS.length)];
        const line = document.createElement('div');
        line.className = 'emoji-line';
        line.innerHTML = emoji;

        // Random position and speed
        const startX = Math.random() * window.innerWidth;
        const duration = 8 + Math.random() * 12; // Faster animation
        const delay = Math.random() * 2; // Shorter delay
        const size = 24 + Math.random() * 24; // Slightly larger emojis

        line.style.left = `${startX}px`;
        line.style.fontSize = `${size}px`;
        line.style.animation = `floatUp ${duration}s linear ${delay}s`;

        containerRef.current.appendChild(line);

        // Remove the element after animation
        setTimeout(() => {
            if (line && line.parentNode === containerRef.current) {
                containerRef.current.removeChild(line);
            }
        }, (duration + delay) * 1000);
    };

    useEffect(() => {
        // Create initial emojis
        for (let i = 0; i < 10; i++) {
            createEmojiLine();
        }

        // Continue creating new emojis
        const interval = setInterval(createEmojiLine, 1000); // More frequent emoji creation
        return () => clearInterval(interval);
    }, []);

    return <div ref={containerRef} className="emoji-background" />;
};

export default EmojiBackground;
