import React from 'react';
import './hero1.css';

const Hero1 = ({ setCurrentPage }) => {
  return (
    <section className="hero">
      <div className="hero-box">
        <h1 className="hero-title">Hello, I'm <span className="highlight">Sarvagya</span></h1>
        <h2 className="hero-subtitle">Electrical Engineering and Computer Science Student</h2>
        <h3 className="hero-university">University of California, <span className="highlight-blue">Berkeley</span></h3>

        <div className="hero-buttons">
          <button className="hero-button" onClick={() => setCurrentPage(1)}>About</button>
          <button className="hero-button" onClick={() => setCurrentPage(2)}>Experiences</button>
          <button className="hero-button" onClick={() => setCurrentPage(3)}>Projects</button>
          <button className="hero-button" onClick={() => setCurrentPage(4)}>Research</button>
          <button className="hero-button" onClick={() => setCurrentPage(5)}>Contact</button>
        </div>
      </div>
    </section>
  );
};

export default Hero1;
