import React from 'react';
import './Hero.css';

const Hero1 = ({ setCurrentPage, prev, next }) => {
  return (
    <section className="hero">
      <h1>Hello, I'm Sarvagya</h1>
      <h2>Electrical Engineering and Computer Science Student</h2>
      <h3>University of California, Berkeley</h3>
      <div className="buttons">
        <button className="button about-me" onClick={() => setCurrentPage(1)}>About</button>
        <button className="button experiences" onClick={() => setCurrentPage(2)}>Experiences</button>
        <button className="button projects" onClick={() => setCurrentPage(3)}>Projects</button>
        <button className="button research" onClick={() => setCurrentPage(4)}>Research</button>
        <button className="button contact" onClick={() => setCurrentPage(5)}>Contact</button>
      </div>
      <div className="navigation-hint top-hint" onClick={() => setCurrentPage(5)}>{prev}</div>
      <div className="navigation-hint bottom-hint" onClick={() => setCurrentPage(1)}>{next}</div>
    </section>
  );
};

export default Hero1;
