import React from 'react';
import './Hero.css';

const Hero1 = ({ setCurrentPage }) => {
  return (
    <section className="hero">
      <h1>Hello, I'm Sarvagya</h1>
      <p>Computer Scientist and Software Developer</p>
      <div className="buttons">
        <button className="button about-me" onClick={() => setCurrentPage(1)}>About Me</button>
        <button className="button experiences" onClick={() => setCurrentPage(2)}>Experiences</button>
        <button className="button projects" onClick={() => setCurrentPage(3)}>Projects</button>
        <button className="button research" onClick={() => setCurrentPage(4)}>Research</button>
      </div>
    </section>
  );
};

export default Hero1;
