import React, { useRef, useState, Fragment } from 'react';
import ReactPageScroller from 'react-page-scroller';
import Hero1 from './components/Hero1';
import Hero2 from './components/Hero2';
import Hero3 from './components/Hero3';
import Hero4 from './components/Hero4';
import Hero5 from './components/Hero5';
import Hero6 from './components/Hero6';
import LinkedInOverlay from './components/LinkedInOverlay';

import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState(0);

  const handlePageChange = (number) => {
    setCurrentPage(number);
  };

  const pages = [
    { component: Hero1, title: "Home" },
    { component: Hero2, title: "About" },
    { component: Hero3, title: "Experiences" },
    { component: Hero4, title: "Projects" },
    { component: Hero5, title: "Research" },
    { component: Hero6, title: "Contact" },
  ];

  return (
    <Fragment>
      <ReactPageScroller
        pageOnChange={handlePageChange}
        customPageNumber={currentPage}
      >
        {pages.map((page, index) => {
          const PageComponent = page.component;
          const prevTitle = pages[index - 1]?.title || pages[pages.length - 1].title;
          const nextTitle = pages[index + 1]?.title || pages[0].title;
          return (
            <PageComponent
              key={index}
              prev={prevTitle}
              next={nextTitle}
              setCurrentPage={setCurrentPage}
            />
          );
        })}
      </ReactPageScroller>
      <LinkedInOverlay />
    </Fragment>
  );
}

export default App;
