// src/components/sections/Experiences.js
import React from 'react';
import './SectionStyles.css';

const Experiences = () => {
    const experiences = [
        {
            title: "Undergraduate Student Researcher",
            organization: "Berkeley Artificial Intelligence Research (BAIR)",
            date: "Jan 2024 - Present",
            description: "Working with Professor Sergey Levine on Robotics and AI research.",
            location: "Berkeley, CA"
        },
        {
            title: "Software Engineering Intern",
            organization: "Anthropic",
            date: "May 2023 - Aug 2023",
            description: "Worked on Constitutional AI and Large Language Model development.",
            location: "San Francisco, CA"
        },
        {
            title: "Software Engineering Intern",
            organization: "Replit",
            date: "May 2022 - Aug 2022",
            description: "Developed AI-powered code completion and generation features.",
            location: "San Francisco, CA"
        }
    ];

    return (
        <div className="section experiences">
            <div className="section-header">
                <h2>experiences</h2>
            </div>
            <div className="section-content">
                {experiences.map((exp, index) => (
                    <div key={index} className="experience-item">
                        <div className="experience-header">
                            <h3>{exp.title}</h3>
                            <span className="experience-date">{exp.date}</span>
                        </div>
                        <div className="experience-subheader">
                            <span className="organization">{exp.organization}</span>
                            <span className="location">{exp.location}</span>
                        </div>
                        <p className="description">{exp.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Experiences;
