// src/components/sections/Research.js
import React from 'react';
import './SectionStyles.css';
import useWindowControls from '../../hooks/useWindowControls';

const Research = () => {
    const { isExpanded, isVisible, TrafficLights } = useWindowControls();

    const research = [
        {
            title: "Robotics and Reinforcement Learning Research",
            lab: "Berkeley Artificial Intelligence Research (BAIR)",
            advisor: "Prof. Sergey Levine",
            description: "Working on advanced robotics control systems using deep learning and reinforcement learning approaches. Focus on developing robust and adaptable robotic systems.",
            date: "2024 - Present"
        },
        {
            title: "Constitutional AI Development",
            organization: "Anthropic",
            description: "Researched and developed AI safety techniques and Constitutional AI frameworks. Worked on ensuring AI systems behave in alignment with specified values and constraints.",
            date: "2023"
        },
        {
            title: "AI Code Generation Research",
            organization: "Replit",
            description: "Researched and developed AI-powered code completion and generation systems. Focused on improving developer productivity through contextual code suggestions.",
            date: "2022"
        }
    ];

    if (!isVisible) return null;

    return (
        <div className={`section research ${isExpanded ? 'expanded' : ''}`}>
            <div className="section-header">
                <TrafficLights />
                <h2>research</h2>
            </div>
            <div className="section-content">
                {research.map((item, index) => (
                    <div key={index} className="research-item">
                        <div className="research-header">
                            <h3>{item.title}</h3>
                            <span className="research-date">{item.date}</span>
                        </div>
                        {item.lab && (
                            <div className="research-lab">
                                <span>{item.lab}</span>
                                {item.advisor && <span className="advisor">Advisor: {item.advisor}</span>}
                            </div>
                        )}
                        {item.organization && (
                            <div className="research-org">{item.organization}</div>
                        )}
                        <p className="description">{item.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Research;
