import React from 'react';
import './Hero.css';
import { 
  AccountTree as ProjectIcon, 
  Build as BuildIcon, 
  ShowChart as ShowChartIcon,
  X as XIcon,
  CameraEnhance as CameraEnhanceIcon,
  VideogameAsset as VideogameAssetIcon,
  Security as SecurityIcon,
  SmartToy as SmartToyIcon
} from '@mui/icons-material';



const Hero4 = ({ setCurrentPage, prev, next }) => {
  const projects = [
    {
      title: "AI Brand Semantics Analyzer",
      date: "Full stack app powered by Node.js, Python, xAPI, Grok LLM and React to track and analyze brand performance on the X platform",
      icon: <XIcon style={{ fontSize: 40 }} />,
    },
    {
      title: "Sales Ordering System",
      date: "Full stack product ordering system that streamlines the entire order management process from inception to fulfillment.",
      icon: <ProjectIcon style={{ fontSize: 40 }} />,
    },
    {
      title: "Stock Prediction App",
      date: "Sentiment Analysis Application which forecast stock trajectories by interpreting media headlines.",
      icon: <ShowChartIcon style={{ fontSize: 40 }} />,
    },
    {
      title: "TutorOne",
      date: "Flutter Mobile application which optimizes Optical Character Recognition to facilitate improvement of legible alphabet writing skills in children",
      icon: <CameraEnhanceIcon style={{ fontSize: 40 }} />,
    },
    {
      title: "Haunted Hallows: AR Video Game",
      date: "Hallowen Themed Flutter Game which used Google ML technology to categorize real life objects",
      icon: <VideogameAssetIcon style={{ fontSize: 40 }} />,
    },
    {
      title: "Secure File Sharing System",
      date: "A Go based secure file storage and sharing system, which ensures confidentiality and integrity through Incorporated cryptographic principles",
      icon: <SecurityIcon style={{ fontSize: 40 }} />,
    },
    {
      title: "Discord Bot",
      date: "Chatbot utilizing various APIs to provide platiude of text-based information using the Discord platform",
      icon: < SmartToyIcon style={{ fontSize: 40 }} />,
    },
    {
      title: "MEGAlib Conversion Project",
      date: "Highly modular data analysis library encompassing of approximately 1000 source files upgraded to CMake build services. ",
      icon: <BuildIcon style={{ fontSize: 40 }} />,
    },
  ];

  return (
    <section className="hero">
      <h1>Projects</h1>
      <div className="projects-grid">
        {projects.map((project, index) => (
          <div className="project-card" key={index}>
            <div className="project-icon">{project.icon}</div>
            <div className="project-details">
            <h2 className="project-title">{project.title}</h2>
              <p className="project-date">{project.date}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="navigation-hint top-hint" onClick={() => setCurrentPage(2)}>{prev}</div>
      <div className="navigation-hint bottom-hint" onClick={() => setCurrentPage(4)}>{next}</div>
    </section>
  );
};

export default Hero4;
