import React, { useEffect, useRef } from 'react';
import './EmojiBackground.css';

// Updated emoji list with more visible options
const EMOJIS = ['üåü', '‚≠ê', 'üí´', '‚ú®', 'üíª', 'üöÄ', 'ü§ñ', '‚ö°', 'üîÆ', 'üéÆ', 'üñ•Ô∏è', 'üì±', '‚öôÔ∏è','üõ†Ô∏è', '</>', 'üìü', 'üì°','·Ø§', 'üîó', 'üìå'];
const MAX_SPEED = 0.5;

const EmojiBackground = () => {
    const containerRef = useRef(null);
    const animationFrameRef = useRef(null);

    const handleMouseMove = (e) => {
        if (!containerRef.current) return;
        const emojis = containerRef.current.getElementsByClassName('emoji-line');
        const mouseX = e.clientX;
        const mouseY = e.clientY;

        Array.from(emojis).forEach(emoji => {
            const rect = emoji.getBoundingClientRect();
            const emojiX = rect.left + rect.width / 2;
            const emojiY = rect.top + rect.height / 2;

            const distX = mouseX - emojiX;
            const distY = mouseY - emojiY;
            const distance = Math.sqrt(distX * distX + distY * distY);

            if (distance < 150) {
                const angle = Math.atan2(distY, distX);
                const pushX = -Math.cos(angle) * (150 - distance) * 0.3;
                const pushY = -Math.sin(angle) * (150 - distance) * 0.3;

                const currentY = parseFloat(emoji.getAttribute('data-y') || 0);
                emoji.style.transform = `translate(${pushX}px, ${currentY + pushY}px)`;
            }
        });
    };

    const createEmojiLine = () => {
        if (!containerRef.current) return;

        const emoji = EMOJIS[Math.floor(Math.random() * EMOJIS.length)];
        const line = document.createElement('div');
        line.className = 'emoji-line';
        line.innerHTML = emoji;

        const startX = Math.random() * window.innerWidth;
        const size = 24 + Math.random() * 20;
        const goingUp = Math.random() > 0.5;
        const baseSpeed = 0.2 + Math.random() * 0.1; // Even smaller speed range

        line.style.left = `${startX}px`;
        line.style.fontSize = `${size}px`;

        let currentPos = goingUp ? window.innerHeight + 100 : -100;
        let lastTime = performance.now();

        const updatePosition = (currentTime) => {
            if (!line.isConnected) return;

            const deltaTime = (currentTime - lastTime) / 16; // Normalize to ~60fps
            lastTime = currentTime;

            const speed = Math.min(baseSpeed * deltaTime, MAX_SPEED);
            currentPos += goingUp ? -speed : speed;

            line.style.transform = `translateY(${currentPos}px)`;
            line.setAttribute('data-y', currentPos);

            if (goingUp && currentPos < -100 || !goingUp && currentPos > window.innerHeight + 100) {
                if (containerRef.current && containerRef.current.contains(line)) {
                    containerRef.current.removeChild(line);
                }
                return;
            }
            animationFrameRef.current = requestAnimationFrame(updatePosition);
        };

        containerRef.current.appendChild(line);
        animationFrameRef.current = requestAnimationFrame(updatePosition);
    };

    useEffect(() => {
        const interval = setInterval(createEmojiLine, 900);
        for (let i = 0; i < 6; i++) createEmojiLine();

        window.addEventListener('mousemove', handleMouseMove);
        return () => {
            clearInterval(interval);
            window.removeEventListener('mousemove', handleMouseMove);
            if (animationFrameRef.current) {
                cancelAnimationFrame(animationFrameRef.current);
            }
        };
    }, []);

    return <div ref={containerRef} className="emoji-background" />;
};

export default EmojiBackground;
