// src/components/sections/Projects.js
import React from 'react';
import './SectionStyles.css';

const Projects = () => {
    const projects = [
        {
            title: "Constitutional AI",
            technologies: ["Python", "PyTorch", "Transformers"],
            description: "Developed frameworks for training language models with ethical constraints and behavioral bounds.",
            link: "https://github.com/anthropics/constitutional-ai"
        },
        {
            title: "Ghostwriter",
            technologies: ["TypeScript", "React", "Machine Learning"],
            description: "Built AI-powered code completion system at Replit, improving developer productivity.",
            link: "https://replit.com/site/ghostwriter"
        },
        {
            title: "Personal Website",
            technologies: ["React", "JavaScript", "CSS"],
            description: "Designed and developed a responsive personal portfolio website with modern UI/UX principles.",
            link: "https://som1shi.github.io"
        }
    ];

    return (
        <div className="section projects">
            <div className="section-header">
                <h2>projects</h2>
            </div>
            <div className="section-content">
                {projects.map((project, index) => (
                    <div key={index} className="project-item">
                        <h3>{project.title}</h3>
                        <div className="technologies">
                            {project.technologies.map((tech, i) => (
                                <span key={i} className="tech-tag">{tech}</span>
                            ))}
                        </div>
                        <p className="description">{project.description}</p>
                        <a href={project.link} target="_blank" rel="noopener noreferrer" className="project-link">
                            View Project â†’
                        </a>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Projects;
