import React, { useEffect, useRef } from 'react';
import './EmojiBackground.css';

// Updated emoji list with more visible options
const EMOJIS = ['üåü', '‚≠ê', 'üí´', '‚ú®', 'üíª', 'üöÄ', 'ü§ñ', '‚ö°', 'üîÆ', 'üéÆ', 'üñ•Ô∏è', 'üì±', '‚öôÔ∏è','üõ†Ô∏è', '</>', 'üìü', 'üì°','·Ø§', 'üîó', 'üìå'];

const EmojiBackground = () => {
    const containerRef = useRef(null);

    const createEmojiLine = () => {
        if (!containerRef.current) return;

        const emoji = EMOJIS[Math.floor(Math.random() * EMOJIS.length)];
        const line = document.createElement('div');
        line.className = 'emoji-line';
        line.innerHTML = emoji;

        const startX = Math.random() * window.innerWidth;
        const duration = 10 + Math.random() * 15;
        const size = 24 + Math.random() * 20;

        line.style.left = `${startX}px`;
        line.style.fontSize = `${size}px`;
        line.style.animation = `floatUp ${duration}s linear infinite`;

        containerRef.current.appendChild(line);
    };

    useEffect(() => {
        for (let i = 0; i < 20; i++) {
            setTimeout(() => createEmojiLine(), i * 100);
        }

        const interval = setInterval(createEmojiLine, 2000);
        return () => clearInterval(interval);
    }, []);

    return <div ref={containerRef} className="emoji-background" />;
};

export default EmojiBackground;
