import React, { useState } from 'react';
import './Hero.css';

import LinkedInIcon from '@mui/icons-material/LinkedIn';


const Hero6 = ({ setCurrentPage, prev, next }) => {
  const [showEmail, setShowEmail] = useState(false);

  const toggleEmail = () => {
    setShowEmail(!showEmail);
  };

  return (
    <section className="hero">
      <h1>Contact</h1>
      <p>
        <button onClick={toggleEmail} className="button contact">
          {showEmail ? 'Hide Email' : 'Show Email'}
        </button>
      </p>
      {showEmail && <p>Email: sarvagya [at] berkeley [dot] edu</p>}
      <div className="navigation-hint top-hint" onClick={() => setCurrentPage(4)}>{prev}</div>
      <div className="navigation-hint bottom-hint" onClick={() => setCurrentPage(0)}>{next}</div>
    </section>
  );
};

export default Hero6;
