import React from "react";
import styled from "@emotion/styled";

export function Hero1() {
  return (
    <HeroContainer>
      <ContentBox>
        <Title>
          Hello, Iâ€™m <span className="highlight">Sarvagya</span>
        </Title>
        <Subtitle>Electrical Engineering and Computer Science Student</Subtitle>
        <UniversityText>
          University of California, <span className="highlight-blue">Berkeley</span>
        </UniversityText>

        <ButtonContainer>
          <Button onClick={() => alert("Navigating to About")}>About</Button>
          <Button onClick={() => alert("Navigating to Experiences")}>Experiences</Button>
          <Button onClick={() => alert("Navigating to Projects")}>Projects</Button>
          <Button onClick={() => alert("Navigating to Research")}>Research</Button>
          <Button onClick={() => alert("Navigating to Contact")}>Contact</Button>
        </ButtonContainer>
      </ContentBox>
    </HeroContainer>
  );
}

const HeroContainer = styled.section`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #fafafa;
`;

const ContentBox = styled.div`
  background-color: #f5f5f5;
  padding: 1.5rem 2rem;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  text-align: left;
  max-width: 350px;
`;

const Title = styled.h1`
  font-size: 1.4rem;
  font-weight: bold;
  color: #333;
  margin: 0;
  line-height: 1.4;
  .highlight {
    font-weight: bold;
  }
`;

const Subtitle = styled.h2`
  font-size: 1rem;
  font-weight: 500;
  color: #666;
  margin-top: 0.3rem;
  margin-bottom: 0.3rem;
  line-height: 1.4;
`;

const UniversityText = styled.h3`
  font-size: 0.9rem;
  color: #666;
  margin: 0;
  line-height: 1.4;
  .highlight-blue {
    color: #0056b3;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.4rem;
  margin-top: 1rem;
`;

const Button = styled.button`
  background-color: #e6e6e6;
  border: none;
  padding: 0.5rem;
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.9rem;
  font-weight: 500;
  text-align: center;
  transition: background-color 0.2s;
  box-shadow: inset 0px -1px 3px rgba(0, 0, 0, 0.15);

  &:hover {
    background-color: #d9d9d9;
  }

  &:active {
    background-color: #c9c9c9;
  }
`;

export default Hero1;
