// src/components/sections/Experiences.js
import React from 'react';
import './SectionStyles.css';
import useWindowControls from '../../hooks/useWindowControls';

const Experiences = () => {
    const { isExpanded, isVisible, TrafficLights } = useWindowControls();

    if (!isVisible) return null;

    const experiences = [
        {
            title: "Software Engineer Intern",
            organization: "Anthropic",
            date: "Summer 2023",
            description: "Worked on developing and improving AI safety techniques. Contributed to the development of Constitutional AI frameworks.",
            location: "San Francisco, CA"
        },
        {
            title: "Software Engineer Intern",
            organization: "Replit",
            date: "Summer 2022",
            description: "Developed AI-powered code completion features. Worked on the Ghostwriter team to improve code generation capabilities.",
            location: "San Francisco, CA"
        },
        {
            title: "Research Assistant",
            organization: "UC Berkeley EECS",
            date: "2022 - Present",
            description: "Working on machine learning research projects focused on robotics and reinforcement learning.",
            location: "Berkeley, CA"
        },
        {
            title: "Teaching Assistant",
            organization: "UC Berkeley CS61A",
            date: "Fall 2022",
            description: "Taught Python programming fundamentals to over 200 students. Led discussion sections and office hours.",
            location: "Berkeley, CA"
        }
    ];

    return (
        <div className={`section experiences ${isExpanded ? 'expanded' : ''}`}>
            <div className="section-header">
                <TrafficLights />
                <h2>experiences</h2>
            </div>
            <div className="section-content">
                {experiences.map((exp, index) => (
                    <div key={index} className="experience-item">
                        <div className="experience-header">
                            <h3>{exp.title}</h3>
                            <span className="experience-date">{exp.date}</span>
                        </div>
                        <div className="experience-subheader">
                            <span className="organization">{exp.organization}</span>
                            <span className="location">{exp.location}</span>
                        </div>
                        <p className="description">{exp.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Experiences;
