// src/components/sections/Projects.js
import React from 'react';
import './SectionStyles.css';
import useWindowControls from '../../hooks/useWindowControls';

const Projects = () => {
    const { isExpanded, isVisible, TrafficLights } = useWindowControls();

    const projects = [
        {
            title: "Ghostwriter",
            technologies: ["Python", "TypeScript", "React", "Machine Learning"],
            description: "Built an AI-powered code completion system at Replit that improves developer productivity by suggesting contextually relevant code completions.",
            link: "https://replit.com/site/ghostwriter"
        },
        {
            title: "Constitutional AI Research",
            technologies: ["Python", "PyTorch", "Transformers", "AI Safety"],
            description: "Contributed to research and development of AI safety techniques and Constitutional AI frameworks at Anthropic.",
            link: "https://www.anthropic.com/"
        },
        {
            title: "Robotics Research",
            technologies: ["Python", "PyTorch", "Robotics", "RL"],
            description: "Working on advanced robotics control systems using deep learning and reinforcement learning at Berkeley AI Research Lab.",
            link: "https://bair.berkeley.edu/"
        },
        {
            title: "Personal Portfolio",
            technologies: ["React", "JavaScript", "CSS"],
            description: "Designed and developed a responsive personal portfolio website with modern UI/UX principles and macOS-inspired design.",
            link: "https://som1shi.github.io"
        }
    ];

    if (!isVisible) return null;

    return (
        <div className={`section projects ${isExpanded ? 'expanded' : ''}`}>
            <div className="section-header">
                <TrafficLights />
                <h2>projects</h2>
            </div>
            <div className="section-content">
                {projects.map((project, index) => (
                    <div key={index} className="project-item">
                        <h3>{project.title}</h3>
                        <div className="technologies">
                            {project.technologies.map((tech, i) => (
                                <span key={i} className="tech-tag">{tech}</span>
                            ))}
                        </div>
                        <p className="description">{project.description}</p>
                        <a href={project.link} target="_blank" rel="noopener noreferrer" className="project-link">
                            View Project â†’
                        </a>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Projects;
